// Union both resource-specific and legacy diagnostic logs
let ResourceSpecificLogs = 
    ApiManagementGatewayLogs
    | where "{ApiManagementService}" == "value::all" or _ResourceId == "{ApiManagementService}"
    | project TimeGenerated, ResponseCode, ResponseTime, BackendResponseTime, ApiId, _ResourceId;
let LegacyLogs = 
    AzureDiagnostics
    | where Category == "GatewayLogs"
    | where "{ApiManagementService}" == "value::all" or ResourceId == "{ApiManagementService}"
    | project TimeGenerated, ResponseCode = toint(responseCode_d), ResponseTime = responseTime_d, BackendResponseTime = backendResponseTime_d, ApiId = apiId_s, _ResourceId = ResourceId;
union ResourceSpecificLogs, LegacyLogs
| summarize TotalRequests = count() by bin(TimeGenerated, 5m)
| sort by TimeGenerated asc

// Union both resource-specific and legacy diagnostic logs
let ResourceSpecificLogs = 
    ApiManagementGatewayLogs
    | where "{ApiManagementService}" == "value::all" or _ResourceId == "{ApiManagementService}"
    | where ResponseCode >= 400
    | project TimeGenerated, ResponseCode, _ResourceId;
let LegacyLogs = 
    AzureDiagnostics
    | where Category == "GatewayLogs"
    | where "{ApiManagementService}" == "value::all" or ResourceId == "{ApiManagementService}"
    | where toint(responseCode_d) >= 400
    | project TimeGenerated, ResponseCode = toint(responseCode_d), _ResourceId = ResourceId;
union ResourceSpecificLogs, LegacyLogs
| summarize ErrorCount = count() by ResponseCode, bin(TimeGenerated, 5m)
| sort by TimeGenerated asc

// Union both resource-specific and legacy diagnostic logs
let ResourceSpecificLogs = 
    ApiManagementGatewayLogs
    | where "{ApiManagementService}" == "value::all" or _ResourceId == "{ApiManagementService}"
    | where ResponseCode >= 400
    | project ApiId, ResponseCode, _ResourceId;
let LegacyLogs = 
    AzureDiagnostics
    | where Category == "GatewayLogs"
    | where "{ApiManagementService}" == "value::all" or ResourceId == "{ApiManagementService}"
    | where toint(responseCode_d) >= 400
    | project ApiId = apiId_s, ResponseCode = toint(responseCode_d), _ResourceId = ResourceId;
union ResourceSpecificLogs, LegacyLogs
| summarize ErrorCount = count() by ApiId, ResponseCode
| top 10 by ErrorCount desc

// Union both resource-specific and legacy diagnostic logs
let ResourceSpecificLogs = 
    ApiManagementGatewayLogs
    | where "{ApiManagementService}" == "value::all" or _ResourceId == "{ApiManagementService}"
    | project ApiId, _ResourceId;
let LegacyLogs = 
    AzureDiagnostics
    | where Category == "GatewayLogs"
    | where "{ApiManagementService}" == "value::all" or ResourceId == "{ApiManagementService}"
    | project ApiId = apiId_s, _ResourceId = ResourceId;
union ResourceSpecificLogs, LegacyLogs
| summarize RequestCount = count() by ApiId
| top 10 by RequestCount desc
